// SPDX-License-Identifier: MIT
pragma solidity ^0.6.6;

import "@chainlink/contracts/src/v0.6/interfaces/AggregatorV3Interface.sol";
import "@chainlink/contracts/src/v0.6/VRFConsumerBase.sol";

contract Lottery is VRFConsumerBase{

    address payable[] public players;
    uint256 public usdEntryFee;
    uint256 fee;
    bytes32 keyhash;
    address payable public recentWinner;
    AggregatorV3Interface internal ethUsdPriceFeed;

    enum LOTTERY_STATE {
        OPEN,
        CLOSED,
        CALCULATE_WINNER
    }
    address owner;
    LOTTERY_STATE public lottery_state;
    event RequestedRandomness(bytes32 requestId);

    constructor (address _priceFeedAddress, address _vrfCoordinator, address _link, uint256 _fee, bytes32 _keyhash) 
    public VRFConsumerBase(_vrfCoordinator,_link)
        {
        owner = msg.sender;
        usdEntryFee = 50 * (10**18) ;
        ethUsdPriceFeed = AggregatorV3Interface(_priceFeedAddress);
        lottery_state = LOTTERY_STATE.CLOSED;
        fee = _fee;
        keyhash = _keyhash;

    }
    function enter() public payable{
        // 50$ minimum
        require (lottery_state == LOTTERY_STATE.OPEN);
        require(msg.value>=getEntranceFee(),"Not enough ETH");
        players.push(msg.sender);
    }

    function getEntranceFee() public view returns (uint256){
        (,int256 price,,,) = ethUsdPriceFeed.latestRoundData();
        uint256 adjustedPrice = uint256(price)*(10**10);
        uint256 costToEnter = (usdEntryFee *(10**18))/ adjustedPrice;
        return costToEnter;

    }

    function startLottery() public {
        require(msg.sender == owner);
        require(lottery_state == LOTTERY_STATE.CLOSED, "Can't start a new lottery");
        lottery_state = LOTTERY_STATE.OPEN;
    }


    function endLottery() public {
        // only owner can access this function

        require(lottery_state == LOTTERY_STATE.OPEN, "Cannot close the lottery" );
        lottery_state= LOTTERY_STATE.CALCULATE_WINNER;
        bytes32 requestId = requestRandomness(keyhash, fee);
        emit RequestedRandomness(requestId);

    }

    function fulfillRandomness(bytes32 _requestId, uint256 _randomness) internal override
    {
        require(lottery_state == LOTTERY_STATE.CALCULATE_WINNER, "YOU ARE NOT THERE YET");
        require(_randomness>0, "random not defines");
        uint256 indexOfWinner = _randomness % players.length;
        recentWinner = players[indexOfWinner];
        recentWinner.transfer(address(this).balance);
        // reset 
        players = new address payable[](0);
        lottery_state = LOTTERY_STATE.CLOSED;
    }

}
